mapper.insert:

	SELECT #{prodLgu} || LPAD(NVL(TO_NUMBER(SUBSTR(MAX(PROD_ID), 5)), 0) + 1, 6, '0')
	FROM PROD
	WHERE PROD_LGU  = #{prodLgu}

INSERT INTO PROD(
	PROD_ID
	, PROD_NAME
	, PROD_LGU
	, PROD_BUYER
	, PROD_COST
	, PROD_PRICE
