	, PROD_COLOR
	, PROD_DELIVERY
	, PROD_UNIT
	, PROD_QTYIN
	, PROD_QTYSALE
	, PROD_MILEAGE
)VALUES(
	#{prodId,jdbcType=VARCHAR}
	, #{prodName,jdbcType=VARCHAR}
	, #{prodLgu,jdbcType=CHAR}
	, #{prodBuyer,jdbcType=CHAR}
	, #{prodCost,jdbcType=NUMERIC}
	, #{prodPrice,jdbcType=NUMERIC}
	, #{prodSale,jdbcType=NUMERIC}
